import React, { useState, useEffect } from 'react';
import { fetchDailyData } from '../../api';
import { Line } from 'react-chartjs-2';
import styles from './Chart.module.css';
import CountryPicker from '../CountryPicker/CountryPicker';

function Chart (country) {
    const [dailyData, setDailyData] = useState();

    useEffect(() => {
        const fetchAPI = async () => {
            setDailyData(await fetchDailyData());
        }
        fetchAPI();
    },[]);
    // country,province, timeline;
    var data;
    if(dailyData)
    {
        /*if(country)
        {
            var {country, province, timeline} = dailyData;
            var {cases, deaths, recovered} = timeline;
        }
        else*/
        var {cases, deaths, recovered} = dailyData;
        
        var a=Object.keys(cases), b=Object.values(cases), c=Object.values(recovered);
        var d=Object.values(deaths)
        //console.log(typeof(a));
        data = {
            labels: a,
            datasets: [
              {
                label: "Infected",
                data: b,
                fill: true,
                borderColor: "#blue"
              },
              {
                label: "Recovered",
                data: c,
                fill: false,
                borderColor: "#green",
                backgroundColor: "rgba(0, 255, 0, 0.5)"
              },
              {
                label: "Death",
                data: d,
                fill: true,
                borderColor:"#red",
                backgroundColor: "rgba(255, 0, 0, 0.5)"
              }
            ]
          };
    }


    if(data)
    return (
        <div className={styles.container}>
            <Line data={data}/>
        </div>
    )
    else
        return (
            <div className={styles.container}>
                
            </div>
        )

}

export default Chart;
