import axios from 'axios';

const url= "https://corona.lmao.ninja/v2/all?yesterday";
const dailyurl= "https://corona.lmao.ninja/v2/historical/India?lastdays=200";

export const fetchdata = async () => {
    try {
        const {data: { cases, recovered, deaths, updated}} = await axios.get(url);

        return { cases, recovered, deaths, updated };
    } catch (error) {
        console.log(error);
    }
}

export const fetchDailyData = async () => {
    try {
        const { data } = await axios.get(dailyurl);
        return data;
        //console.log(typeof(data));
    }catch(error) {
        console.log(error);
    }
}
export const countries = async () => {
    try{
        const {data : { countries }} = await axios.get('https://corona.lmao.ninja/v2/countries?yesterday=true&strict=true&query')

