            labels: a,
            datasets: [
              {
                label: "Infected",
                data: b,
                fill: true,
                borderColor: "#blue"
              },
              {
                label: "Recovered",
                data: c,
                fill: false,
                borderColor: "#green",
                backgroundColor: "rgba(0, 255, 0, 0.5)"
              },
              {
                label: "Death",
                data: d,
                fill: true,
                borderColor:"#red",
                backgroundColor: "rgba(255, 0, 0, 0.5)"
              }
            ]
          };
    }

