import React, { useState, useEffect } from 'react';
import { fetchDailyData } from '../../api';
import Line from 'react-chartjs-2';
import styles from './Chart.module.css';

function Chart() {
    const dailyurl= "https://corona.lmao.ninja/v2/historical/India?lastdays=70";
    const [dailyData, setDailyData] = useState({});

    useEffect(() => {
        const fetchAPI = async () => {
            setDailyData(await fetchDailyData());
        }
        
        fetchAPI();
        
    });
    if(dailyData)
        console.log(dailyData);
    const lineChart = {
        /*dailyData.length ?
        (
            <line
            data={{
                labels:dailyData(({date}) => date),
                datasets: [{
                    data: dailyData(({cases}) => cases),
                    label: 'Infected',
                    borderColor: '#3333ff',
                    fill: true,
                }, {
                    dailyData(({recovered}) => recovered),
                    label: 'recovered',
                    borderColor: '#3333ff',
                    fill: true,
                }, {
                    dailyData(({deaths}) => deaths),
                    label: 'deaths',
                    borderColor: '#red',
                    backgroundColor:rgba(255, 0, 0, 0.5),
                    fill: true,
                }],
            }}
            /> : null
        )*/
    };
    return (
        <div className={styles.container}>

        </div>
    )
}

export default Chart;
