import axios from 'axios';

const url= "https://corona.lmao.ninja/v2/all?yesterday";
const dailyurl= "https://corona.lmao.ninja/v2/historical/India?lastdays=70";

export const fetchdata = async () => {
    try {
        const {data: { cases, recovered, deaths, updated}} = await axios.get(url);

        return { cases, recovered, deaths, updated };
    } catch (eror) {

    }
}

export const fetchDailyData = async () => {
    try {
        const { data: {country, province, timeline, ...rest} } = await axios.get(dailyurl);
        const {cases, deaths, recovered} = {timeline};
        console.log(typeof(cases));
        return {cases, deaths, recovered};
        //return data;
        //console.log(typeof(data));
    }catch(eror) {

    }

}
